			KeyGenerator keyGenerator = KeyGenerator.getInstance("HmacMD5");
			SecretKey secretKey = keyGenerator.generateKey();// 
			byte[] key = secretKey.getEncoded();// 

			key = Hex.decodeHex(keyParameter.toCharArray());//

