		KeyParameter k = new KeyParameter(b);
		hmac.init(k);
		hmac.update(msg.getBytes(), 0, msg.getBytes().length);

		byte[] hmacMD5Bytes = new byte[hmac.getMacSize()];
		hmac.doFinal(hmacMD5Bytes, 0);

