public void increase() {
	try {
		Thread.sleep(100);
	} catch (InterruptedException e) {
		e.printStackTrace();
	}
	synchronized (this) {
		this.number++;
	}
}

// ReentrantLock 
private Lock lock = new ReentrantLock();
public void increase() {
	try {
		Thread.sleep(100);
	} catch (InterruptedException e) {
		e.printStackTrace();
	}
	lock.lock();//
	try {
		this.number++;
	} finally {
		lock.unlock();// .try.JDKtry..finally
	}
}

